import java.math.BigDecimal;
import java.math.MathContext;
import java.math.RoundingMode;
import java.util.Scanner;

public class Main {
	public static final MathContext mc = new MathContext(1000, RoundingMode.HALF_UP);

	public static final BigDecimal   PI = new BigDecimal("3.");
	public static final BigDecimal  SQ2 = new BigDecimal("1.");
	public static final BigDecimal SQPI = new BigDecimal("1.");
	
	public static final BigDecimal TWO = new BigDecimal("2");
	public static final BigDecimal MINUS_ONE = new BigDecimal("-1");
	public static final BigDecimal HALF = new BigDecimal("0.5");

	public static void main(String[] args) {

		Scanner sc = new Scanner(System.in);
		System.out.print("Enter value of positive integer parameter 'k': ");
		int k = sc.nextInt();
		System.out.print("Enter value of nonnegative real parameter 'g': ");
		double g = sc.nextDouble();
		System.out.print("How many decimal digits do you want to see? ");
		int scale = sc.nextInt();

		BigDecimal[] f0 = vec_f(k,g);
		BigDecimal[][] f = new BigDecimal[f0.length][1];
		for (int i=0; i<f0.length; i++) {
			f[i][0] = f0[i];
		}
		BigDecimal[][] C = matrix_C(k);
		BigDecimal[][] B = matrix_B(k);
		BigDecimal[][] D = matrix_D(k);
		
		BigDecimal[][] result0 = matmul(matmul(matmul(D,B),C),f);
		BigDecimal[] result = new BigDecimal[result0.length];

		for (int i=0; i<result0.length; i++) {
    		result[i] = result0[i][0];
		}	

		for (BigDecimal a : result) {
			System.out.println(a.setScale(scale, RoundingMode.HALF_UP));
		} 
	}


	public static BigDecimal gammaPlusHalf(int A) {
		BigDecimal result = BigDecimal.ONE;
		for (int i=1; i <= A; i++) {
			result = result.multiply((TWO.multiply(BigDecimal.valueOf(i))).subtract(BigDecimal.ONE));
		}
		BigDecimal finalResult = result.multiply(SQPI.divide(TWO.pow(A), mc));
		return finalResult;
	}

	public static BigDecimal factorial(int n) {
		if (n <= 0) {
			return BigDecimal.ONE;
		}
		return (BigDecimal.valueOf(n)).multiply(factorial(n-1));
	}

	public static BigDecimal expE(BigDecimal big) {
		BigDecimal result = BigDecimal.ONE.add(big); 

		int terms = 500; // make this as small or large as needed.
		for (int i=2; i<terms; i++) {
			BigDecimal bottom = factorial(i);
			BigDecimal top = big.pow(i);
			result = result.add(top.divide(bottom, mc));
		}
		//return result.pow(s1, mc);
		return result;
	}

	public static BigDecimal helper1(BigDecimal big, int n) { // (BigDecimal)^(int + 1/2)
		BigDecimal t1 = big.pow(n);
		BigDecimal root = big.sqrt(mc);
		return t1.multiply(root);
	}
	
	public static BigDecimal helper2(int A, double g) {
		BigDecimal GG = BigDecimal.valueOf(g);
		BigDecimal t0 = SQ2.divide(PI, mc);
		BigDecimal t1 = helper1((BigDecimal.valueOf(A)).add(GG).add(HALF), A);
		BigDecimal t2 = expE((BigDecimal.valueOf(A)).add(GG).add(HALF));
		BigDecimal t3 = gammaPlusHalf(A);

		return (t0.multiply(t2).multiply(t3)).divide(t1, mc);
	}

	public static BigDecimal[] vec_f(int k, double g) {
		BigDecimal[] result = new BigDecimal[k+1];
		for (int i=0; i<=k; i++) {
			result[i] = helper2(i, g);
		}
		return result;
	}

	public static BigDecimal[][] chebyshevs(int k) {
		int u = 2*k+1;
		BigDecimal[][] dp = new BigDecimal[u][u];
		for (int i=0; i<u; i++) {
			for (int j=0; j<u; j++) {
				dp[i][j] = BigDecimal.ZERO;
			}
		}
		
		dp[0][0] = BigDecimal.ONE;
		dp[1][1] = BigDecimal.ONE;

		for (int i=2; i<u; i++) {
			dp[i][i] = TWO.multiply(dp[i-1][i-1]);
			dp[i][0] = (dp[i-2][0]).negate();
		}

		for (int j=1; j<u; j++) {
			for (int i=j+1; i<u; i++) {
				dp[i][j] = (TWO.multiply(dp[i-1][j-1])).subtract(dp[i-2][j]);
			}
		}
		return dp;
	}

	public static BigDecimal[][] matrix_C(int k) {
		BigDecimal[][] C = new BigDecimal[k+1][k+1];
		for (int i=0; i<=k; i++) {
			for (int j=0; j<=k; j++) {
				C[i][j] = BigDecimal.ZERO;
			}
		}
		BigDecimal[][] mat = chebyshevs(k);
		
		for (int i=0; i<=k; i++) {
			for (int j=0; j<=k; j++) {
				C[i][j] = mat[2*i][2*j];
			}
		}
		C[0][0] = HALF;
		return C;
	}

	public static BigDecimal[][] matrix_B(int k) {
		BigDecimal[][] B = new BigDecimal[k+1][k+1];
		for (int i=0; i<=k; i++) {
			for (int j=0; j<=k; j++) {
				B[i][j] = BigDecimal.ZERO;
			}
		}

		for (int i=0; i<=k; i++) {
			for (int j=i; j<=k; j++) {
				if (i == 0) {
					B[i][j] = BigDecimal.ONE;
					continue;
				}
				BigDecimal v1 = (MINUS_ONE.pow(j-i)).multiply(factorial(j+i-1));
				BigDecimal v2 = factorial(2*i - 1).multiply(factorial(j-i));
				B[i][j] = v1.divide(v2, mc);
			}
		}
		return B;
	}

	public static BigDecimal[][] matrix_D(int k) {
		BigDecimal[][] D = new BigDecimal[k+1][k+1];
		for (int i=0; i<=k; i++) {
			for (int j=0; j<=k; j++) {
				D[i][j] = BigDecimal.ZERO;
			}
		}

		for (int i=0; i<=k; i++) {
			if (i == 0) {
				D[i][i] = BigDecimal.ONE;
				continue;
			}
			BigDecimal v1 = factorial(2*i - 1).negate();
			BigDecimal v2 = (factorial(i-1)).pow(2);
			D[i][i] = v1.divide(v2, mc);
		}
		return D;
	}

	public static BigDecimal[][] matmul(BigDecimal[][] A, BigDecimal[][] B) { 
		int m = A.length;
		int n = A[0].length;
		int p = B[0].length;

		BigDecimal[][] C = new BigDecimal[m][p];

		for (int i = 0; i < m; i++) {
            for (int j = 0; j < p; j++) {
                BigDecimal sum = BigDecimal.ZERO;
                for (int k = 0; k < n; k++) {
                    sum = sum.add(A[i][k].multiply(B[k][j]));
                }
                C[i][j] = sum;
            }
        }
        return C;
	} 
}